<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    // Handle POST request\n    // You can access POST data using the $_POST superglobal array\n    $postData = $_POST['your_post_key'] ?? 'default_value';\n    echo "This is a POST request. Data received: " . htmlspecialchars($postData);\n} elseif ($_SERVER['REQUEST_METHOD'] === 'GET') {\n    // Handle GET request\n    // You can access GET data using the $_GET superglobal array\n    $getData = $_GET['your_get_key'] ?? 'default_value';\n    echo "This is a GET request. Data received: " . htmlspecialchars($getData);\n} else {\n    echo "Unsupported request method.";\n}\n?>