<?php\nrequire_once("../includes/class.database.php");\nrequire_once("../includes/dbConnectInfo.inc");\nrequire_once("../includes/functions.php");\ninclude("../includes/conn.php");\n\n$cat = $_GET['category'] ?? '';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password, $options);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $pdo->prepare("SELECT * FROM media WHERE related_page_id = :related_page_id AND type = :type ORDER BY id ASC LIMIT 6");\n    $stmt->execute(['related_page_id' => 4, 'type' => 'copy']);\n    $media = "<?xml version=\"1.0\" encoding=\"ISO-8859-1\" ?>\n<content>\n";\n\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        $media .= "<member>\n";\n        $body = $row['copy'];\n\n        $title = $row['title'] ?? '';\n        if ($title == "") {\n            $media .= "<title><![CDATA[Team]]></title>\n";\n        } elseif ($row['path'] == "") {\n            $name = explode("/", $title, 2);\n            $media .= "<name><![CDATA[" . $name[0] . "]]></name>\n";\n            $media .= "<job><![CDATA[" . $name[1] . "]]></job>\n";\n        }\n\n        if ($body != "") {\n            $media .= "<bio><![CDATA[" . $body . "]]></bio>\n";\n        }\n\n        $something = $row['id'];\n        $stmt1 = $pdo->prepare("SELECT * FROM media WHERE assets = :assets");\n        $stmt1->execute(['assets' => $something]);\n        $media .= "<images>\n";\n\n        while ($row1 = $stmt1->fetch(PDO::FETCH_ASSOC)) {\n            $img = explode("/", $row1['path'], 2);\n            $media .= "<image url='" . htmlspecialchars($img[1], ENT_QUOTES, 'ISO-8859-1') . "' />\n";\n        }\n        $media .= "</images>\n";\n        $media .= "</member>\n";\n    }\n    $media .= "</content>";\n    echo $media;\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>