<?php\n$content = '<p>John Doe was born on...</p><img src="/jd.jpg" alt="John Doe at the beach" />';\n\n// Pattern to find "John Doe" not within an alt attribute\n$pattern = '/(?!<[^>]*alt="[^"]*John Doe[^"]*")\bJohn Doe\b/';\n\n// Replacement string\n$replacement = 'Jane Smith';\n\n// Perform the replacement\n$updated_content = preg_replace($pattern, $replacement, $content);\n\necho $updated_content;\n?>