function removeValueFromArray($array, $valueToRemove) {\n    // Use array_filter to remove the specified value\n    $filteredArray = array_filter($array, function($value) use ($valueToRemove) {\n        return $value !== $valueToRemove;\n    });\n\n    // Re-index the array\n    return array_values($filteredArray);\n}\n\n// Example usage:\n$array = [1, 2, 3, 4, 2, 5, 2];\n$valueToRemove = 2;\n$result = removeValueFromArray($array, $valueToRemove);\n\nprint_r($result);\n// Output: Array ( [0] => 1 [1] => 3 [2] => 4 [3] => 5 )