<?php\n// Create a new PDO instance\n$dsn = 'mysql:host=your_host;dbname=your_dbname';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n    \n    // Set the default fetch mode to FETCH_ASSOC\n    $pdo->setAttribute(PDO::ATTR_DEFAULT_FETCH_MODE, PDO::FETCH_ASSOC);\n\n    // Execute a query\n    $stmt = $pdo->query("SELECT int_column, float_column FROM your_table");\n\n    // Fetch the results\n    while ($row = $stmt->fetch()) {\n        // Cast the values to the appropriate types\n        $intColumn = (int)$row['int_column'];\n        $floatColumn = (float)$row['float_column'];\n\n        // Use the casted values as needed\n        echo "Integer: $intColumn, Float: $floatColumn\n";\n    }\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>