$jsonurl = "http://search.twitter.com/trends.json";\n$json = file_get_contents($jsonurl, false, stream_context_create([\n    'http' => [\n        'timeout' => 5, // Set a timeout\n    ]\n]));\n\nif ($json === false) {\n    die('Error fetching the JSON data.');\n}\n\n$json_output = json_decode($json);\nif (json_last_error() !== JSON_ERROR_NONE) {\n    die('Error decoding JSON: ' . json_last_error_msg());\n}\n\n$trendNames = [];\nif (isset($json_output->trends) && is_array($json_output->trends)) {\n    foreach ($json_output->trends as $trend) {\n        if (isset($trend->name)) {\n            $trendNames[] = $trend->name;\n        }\n    }\n}\n\n// Output the trend names\nforeach ($trendNames as $name) {\n    echo htmlspecialchars($name, ENT_QUOTES, 'UTF-8') . "<br>";\n}