<?php\n// Path to the image\n$imagePath = 'path/to/your/image.jpg';\n\n// Load the image\n$image = imagecreatefromjpeg($imagePath);\nif (!$image) {\n    die('Failed to load image');\n}\n\n// Get image dimensions\n$width = imagesx($image);\n$height = imagesy($image);\n\n// Convert to grayscale\nimagefilter($image, IMG_FILTER_GRAYSCALE);\n\n// Define the threshold for black and white\n$threshold = 128; // Adjust this value if needed\n\n// Loop through each pixel and apply threshold\nfor ($y = 0; $y < $height; $y++) {\n    for ($x = 0; $x < $width; $x++) {\n        $gray = imagecolorat($image, $x, $y) & 0xFF;\n        if ($gray < $threshold) {\n            // Set pixel to black\n            imagesetpixel($image, $x, $y, imagecolorallocate($image, 0, 0, 0));\n        } else {\n            // Set pixel to white\n            imagesetpixel($image, $x, $y, imagecolorallocate($image, 255, 255, 255));\n        }\n    }\n}\n\n// Save the new image\n$outputPath = 'path/to/your/output_image.jpg';\nimagejpeg($image, $outputPath);\n\n// Free up memory\nimagedestroy($image);\n\necho "Image successfully converted to black and white.";\n?>