<?php\n// Using PDO with persistent connections\nfunction getDbConnection($dbName) {\n    static $connections = [];\n    \n    if (isset($connections[$dbName])) {\n        return $connections[$dbName];\n    }\n    \n    $dsn = "mysql:host=your_host;dbname=$dbName";\n    $username = 'your_username';\n    $password = 'your_password';\n    $options = [\n        PDO::ATTR_PERSISTENT => true,\n        PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION\n    ];\n    \n    try {\n        $connections[$dbName] = new PDO($dsn, $username, $password, $options);\n    } catch (PDOException $e) {\n        die("Connection failed: " . $e->getMessage());\n    }\n    \n    return $connections[$dbName];\n}\n\n// Example usage\n$globalDb = getDbConnection('global_db');\n$clientDb = getDbConnection('client_db');\n\n// Perform queries on global_db\n$query = $globalDb->query("SELECT * FROM global_settings");\n$globalSettings = $query->fetchAll(PDO::FETCH_ASSOC);\n\n// Perform queries on client_db\n$query = $clientDb->query("SELECT * FROM client_data");\n$clientData = $query->fetchAll(PDO::FETCH_ASSOC);\n?>