$ar = array(\n    array(1, 1, 7.50, 'Hello'),\n    array(1, 2, 18.90, 'Hello'),\n    array(3, 5, 11.50, 'Hello'),\n    array(2, 4, 15.90, 'Hello')\n);\n\n// Extract the third column values\n$column = array_column($ar, 2);\n\n// Sort the values in descending order\nrsort($column);\n\n// Get the top two values\n$topTwo = array_slice($column, 0, 2);\n\n// Output the result\nforeach ($topTwo as $value) {\n    echo $value . "\n";\n}