<?php\n// Create a new Memcached instance\n$memcached = new Memcached();\n\n// Add a server to the pool\n$memcached->addServer('localhost', 11211);\n\n// Set the distribution option to consistent hashing (ketama)\n$memcached->setOption(Memcached::OPT_DISTRIBUTION, Memcached::DISTRIBUTION_CONSISTENT);\n\n// Additional options can be set as needed\n// e.g., enable binary protocol\n$memcached->setOption(Memcached::OPT_BINARY_PROTOCOL, true);\n\n// Add and retrieve a value to test the configuration\n$memcached->set('key', 'value');\n$value = $memcached->get('key');\necho $value; // Outputs: value\n?>