<?php\nfunction sendTrackback($trackback_url, $post_url, $post_title, $post_excerpt, $blog_name) {\n    $data = http_build_query([\n        'url' => $post_url,\n        'title' => $post_title,\n        'excerpt' => $post_excerpt,\n        'blog_name' => $blog_name,\n    ]);\n\n    $options = [\n        'http' => [\n            'header' => "Content-type: application/x-www-form-urlencoded\r\n",\n            'method' => 'POST',\n            'content' => $data,\n        ],\n    ];\n\n    $context = stream_context_create($options);\n    $result = file_get_contents($trackback_url, false, $context);\n\n    return $result !== false;\n}\n\n// Usage example:\n$trackback_url = 'http://example.com/trackback';\n$post_url = 'http://yourblog.com/post/123';\n$post_title = 'Your Blog Post Title';\n$post_excerpt = 'An excerpt of your blog post';\n$blog_name = 'Your Blog Name';\n\nif (sendTrackback($trackback_url, $post_url, $post_title, $post_excerpt, $blog_name)) {\n    echo 'Trackback sent successfully.';\n} else {\n    echo 'Failed to send trackback.';\n}\n?>