public function yourAjaxFunction() {\n    // Your logic here\n\n    // Check if you need to redirect\n    if ($someCondition) {\n        $this->response->type('json');\n        $this->response->body(json_encode(['redirect' => Router::url(['controller' => 'YourController', 'action' => 'yourAction'])]));\n        return $this->response;\n    }\n\n    // Otherwise, return your usual AJAX response\n    $this->response->type('json');\n    $this->response->body(json_encode(['data' => 'your usual data']));\n    return $this->response;\n}