<?php\nfunction isCrawler() {\n    $userAgent = strtolower($_SERVER['HTTP_USER_AGENT']);\n    $crawlers = array(\n        'googlebot',\n        'bingbot',\n        'yandex',\n        'baiduspider',\n        'facebookexternalhit',\n        'twitterbot',\n        'ia_archiver',\n        'facebot',\n        'slurp',\n        'duckduckbot'\n    );\n    \n    foreach ($crawlers as $crawler) {\n        if (strpos($userAgent, $crawler) !== false) {\n            return true;\n        }\n    }\n    return false;\n}\n\nif (isCrawler()) {\n    // Render the full content for crawlers\n    include('speakers.php');\n} else {\n    // Render the main page\n    include('main_page.php');\n}\n?>