<?php\nfunction getTrackbacks($post_id) {\n    // Fetch trackbacks from the database for the given post ID\n    // Example: return fetchTrackbacksFromDatabase($post_id);\n    return [\n        ['url' => 'http://example1.com', 'title' => 'Example 1', 'excerpt' => 'Excerpt 1', 'blog_name' => 'Blog 1'],\n        ['url' => 'http://example2.com', 'title' => 'Example 2', 'excerpt' => 'Excerpt 2', 'blog_name' => 'Blog 2'],\n    ];\n}\n\n$post_id = 123; // Example post ID\n$trackbacks = getTrackbacks($post_id);\n\nforeach ($trackbacks as $trackback) {\n    echo '<div class="trackback">';\n    echo '<h3><a href="' . htmlspecialchars($trackback['url']) . '">' . htmlspecialchars($trackback['title']) . '</a></h3>';\n    echo '<p>' . htmlspecialchars($trackback['excerpt']) . ' - ' . htmlspecialchars($trackback['blog_name']) . '</p>';\n    echo '</div>';\n}\n?>