<?php\n\n$xml  = file_get_contents('sample.xml');\n$json = file_get_contents('sample.json');\n\n$iters = 1000;\n\n// simplexml_load_string\n$start_xml = hrtime(true);\nfor ($i = 0; $i < $iters; ++$i) {\n    $obj = simplexml_load_string($xml, 'SimpleXMLElement', LIBXML_NOCDATA);\n}\n$end_xml = hrtime(true);\n\n// json_decode\n$start_json = hrtime(true);\nfor ($i = 0; $i < $iters; ++$i) {\n    $obj = json_decode($json);\n}\n$end_json = hrtime(true);\n\n$xml_elapsed = ($end_xml - $start_xml) / 1e+6; // Convert nanoseconds to milliseconds\n$json_elapsed = ($end_json - $start_json) / 1e+6; // Convert nanoseconds to milliseconds\n\n?>\n\n<pre>XML elapsed: <?=sprintf('%.4f ms', $xml_elapsed)?></pre>\n<pre>JSON elapsed: <?=sprintf('%.4f ms', $json_elapsed)?></pre>