// PHP example\n\n// Get the minimum and maximum ID\n$result = mysqli_query($conn, "SELECT MIN(id) AS min_id, MAX(id) AS max_id FROM your_table");\n$row = mysqli_fetch_assoc($result);\n\n$min_id = $row['min_id'];\n$max_id = $row['max_id'];\n\n// Generate a random ID within the range\n$random_id = rand($min_id, $max_id);\n\n// Select the random row\n$result = mysqli_query($conn, "SELECT * FROM your_table WHERE id >= $random_id LIMIT 1");\n\n// Fetch the row\n$random_row = mysqli_fetch_assoc($result);