$xmlString = '\n<data>\n    <seg id="A1"/>\n    <seg id="A5"/>\n    <seg id="A12"/>\n    <seg id="A29"/>\n    <seg id="A30"/>\n</data>';\n\n$doc = simplexml_load_string($xmlString);\n\n// Find the parent node\n$parent = $doc;\n\n// Loop through 'seg' elements and find the one to remove\nforeach ($parent->seg as $index => $seg) {\n    if ((string) $seg['id'] === 'A12') {\n        // Remove the element by its index\n        unset($parent->seg[$index]);\n        break;\n    }\n}\n\n// Output the modified XML\necho $doc->asXML();