<?php\n\nrequire_once 'Zend/Search/Lucene.php';\nrequire_once 'Zend/Controller/Front.php';\nrequire_once 'Zend/View.php';\n\n// Define the path to the directory containing your view scripts\n$viewPath = APPLICATION_PATH . '/views/scripts';\n$indexPath = APPLICATION_PATH . '/data/lucene_index';\n\n// Create or open the Lucene index\n$index = Zend_Search_Lucene::create($indexPath);\n\n// Function to recursively read view files and add their contents to the index\nfunction indexViews($path, $index)\n{\n    $dir = new DirectoryIterator($path);\n\n    foreach ($dir as $fileinfo) {\n        if ($fileinfo->isDot()) {\n            continue;\n        }\n\n        if ($fileinfo->isDir()) {\n            indexViews($fileinfo->getPathname(), $index);\n        } else {\n            $filePath = $fileinfo->getPathname();\n            $viewContent = file_get_contents($filePath);\n\n            // Create a new document\n            $doc = new Zend_Search_Lucene_Document();\n\n            // Add file path and content to the document\n            $doc->addField(Zend_Search_Lucene_Field::Text('path', $filePath));\n            $doc->addField(Zend_Search_Lucene_Field::Text('content', $viewContent));\n\n            // Add the document to the index\n            $index->addDocument($doc);\n\n            echo "Indexed: " . $filePath . "\n";\n        }\n    }\n}\n\n// Start indexing the views directory\nindexViews($viewPath, $index);\n\n// Optimize the index for better performance\n$index->optimize();\n\necho "Indexing completed.\n";