<?php\n// Persistent MySQL connection\n$mysqli = new mysqli('p:hostname', 'username', 'password', 'database');\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n// Perform a query\n$result = $mysqli->query("SELECT * FROM example_table");\n\nif ($result) {\n    while ($row = $result->fetch_assoc()) {\n        echo $row['example_column'];\n    }\n    $result->free();\n}\n\n// Close the connection\n$mysqli->close();\n\n// Fetching a file using cURL\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, 'http://example.com/file');\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 5);\n$data = curl_exec($ch);\n\nif ($data === false) {\n    echo 'cURL Error: ' . curl_error($ch);\n} else {\n    echo $data;\n}\n\ncurl_close($ch);\n?>