// Ensure the input string is in UTF-8\n$string = mb_convert_encoding($string, 'UTF-8', 'auto');\n\n// Loop through the translated string array and replace the msgstr\nfor(/*extracting from the translated string array*/) {\n    $pattern = "/msgid \"" . preg_quote($id, '/') . "\"(\r?\n)msgstr \"\"/";\n    $string = str_replace('"', '\"', $string);\n    $replacement = "msgid \"$id\"\nmsgstr \"" . $string . "\"";\n    $res = preg_replace($pattern, $replacement, $con);\n    $con = $res;\n}\n\n// Write the content to ja.po file in UTF-8 encoding\nfile_put_contents("ja.po", $con, LOCK_EX | FILE_TEXT);