<?php\n// Database connection (replace with your actual database connection details)\n$pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n\n// Fetch and sanitize the 'max' parameter\n$max = isset($_GET['max']) ? intval($_GET['max']) : 5;  // Default to 5 if not set or invalid\n$max = max(5, $max);  // Ensure 'max' is at least 5\n\n// Fetch the total number of images\n$stmt = $pdo->query('SELECT COUNT(*) FROM images');\n$REAL_MAX = $stmt->fetchColumn();\n\n// Calculate the minimum value as the next number down that's easily divisible by 5\n$min = $max - 5;\n$min = $min - ($min % 5);  // Adjust $min to be divisible by 5\n\n// Ensure $min is within valid range\n$min = max(0, $min);\n\n// Fetch images from the database\n$stmt = $pdo->prepare('SELECT * FROM images LIMIT :min, 5');\n$stmt->bindValue(':min', $min, PDO::PARAM_INT);\n$stmt->execute();\n$images = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n?>\n\n<!DOCTYPE html>\n<html lang="en">\n<head>\n    <meta charset="UTF-8">\n    <title>Image Gallery</title>\n</head>\n<body>\n    <h1>Image Gallery</h1>\n    <div class="gallery">\n        <?php foreach ($images as $image): ?>\n            <div class="image">\n                <img src="<?php echo htmlspecialchars($image['url']); ?>" alt="Image">\n            </div>\n        <?php endforeach; ?>\n    </div>\n\n    <div class="pagination">\n        <?php if ($min > 0): ?>\n            <a href="?max=<?php echo $min; ?>">Previous</a>\n        <?php endif; ?>\n        \n        <?php if ($max < $REAL_MAX): ?>\n            <a href="?max=<?php echo $max + 5; ?>">Next</a>\n        <?php endif; ?>\n    </div>\n</body>\n</html>