<?php\n// Path to the uploaded file\n$filePath = $_FILES['uploaded_file']['tmp_name'];\n\n// Create a new finfo object\n$finfo = finfo_open(FILEINFO_MIME_TYPE);\n\n// Get the MIME type of the file\n$mimeType = finfo_file(finfo, $filePath);\n\n// Close the finfo object\nfinfo_close($finfo);\n\n// Define allowed MIME types\n$allowedMimeTypes = [\n    'image/jpeg',\n    'image/png',\n    'application/pdf'\n    // Add other allowed MIME types here\n];\n\nif (in_array($mimeType, $allowedMimeTypes)) {\n    echo "File type is valid: $mimeType";\n    // Proceed with storing the file\n} else {\n    echo "Invalid file type: $mimeType";\n    // Handle the invalid file type\n}\n?>