<?php\n   require 'vendor/autoload.php';\n\n   $email = new \SendGrid\Mail\Mail();\n   $email->setFrom("your-email@example.com", "Your Name");\n   $email->setSubject("Sending with SendGrid is Fun");\n   $email->addTo("recipient@example.com", "Recipient Name");\n   $email->addContent("text/plain", "and easy to do anywhere, even with PHP");\n   $email->addContent("text/html", "<strong>and easy to do anywhere, even with PHP</strong>");\n\n   // Enable tracking\n   $trackingSettings = new \SendGrid\Mail\TrackingSettings();\n   $clickTracking = new \SendGrid\Mail\ClickTracking(true, true);\n   $trackingSettings->setClickTracking($clickTracking);\n   $openTracking = new \SendGrid\Mail\OpenTracking(true, null);\n   $trackingSettings->setOpenTracking($openTracking);\n   $email->setTrackingSettings($trackingSettings);\n\n   $sendgrid = new \SendGrid(getenv('SENDGRID_API_KEY'));\n   try {\n       $response = $sendgrid->send($email);\n       echo 'Email sent successfully';\n       print $response->statusCode();\n       print_r($response->headers());\n       print $response->body();\n   } catch (Exception $e) {\n       echo 'Caught exception: ',  $e->getMessage(), "\n";\n   }