// The definition of what to call\n$function_call_spec = array(\n    "prototype"  => "myFunction",\n    "parameters" => array("Hello", "World")\n);\n\n// Define the function\nfunction myFunction($first_par, $second_par) {\n    echo $first_par . " " . $second_par;\n}\n\n// Dispatch\n$funcPrototype = $function_call_spec["prototype"];\n$parameters = $function_call_spec["parameters"];\n\n// Dynamically call the function with parameters\ncall_user_func_array($funcPrototype, $parameters);