session_start();\n\n$first_time = (int)date('Hi');\nif (!isset($_SESSION['mypile'])) {\n    $_SESSION['mypile'] = array_fill(0, 15, array('paramA' => 0, 'paramB' => 0));\n}\n\nfunction updateData($index) {\n    // Replace with your actual calculation\n    return array(\n        'paramA' => rand(0, 100),\n        'paramB' => rand(0, 100)\n    );\n}\n\n$time = (int)date('Hi');\n$index = ($time / 10) % 15; // Calculate the index based on time\n\n$_SESSION['mypile'][$index] = updateData($index);\n\n// Example of sending data via Ajax\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    header('Content-Type: application/json');\n    echo json_encode($_SESSION['mypile']);\n}