<?php\n// Set script execution time limit to 0 so it can run indefinitely\nset_time_limit(0);\n\n// Simulate a data source (e.g., a file or database)\n$dataSource = 'data.txt';\n\n// Function to check for new data\nfunction checkForUpdates($lastUpdate) {\n    global $dataSource;\n    clearstatcache();\n    $lastModified = filemtime($dataSource);\n    if ($lastModified > $lastUpdate) {\n        return true;\n    }\n    return false;\n}\n\n// Get the last update time from the client\n$lastUpdate = isset($_GET['timestamp']) ? (int)$_GET['timestamp'] : 0;\n\nwhile (true) {\n    if (checkForUpdates($lastUpdate)) {\n        $data = file_get_contents($dataSource);\n        echo json_encode(['timestamp' => time(), 'data' => $data]);\n        break;\n    }\n    sleep(1); // Sleep for 1 second before checking again\n}\n?>