<?php\n// Establish a persistent connection\n$conn = oci_pconnect('username', 'password', 'dbname');\n\n// Create a Global Temporary Table (only needed once)\n$query_create = "\n    CREATE GLOBAL TEMPORARY TABLE my_temp_table (\n        id NUMBER,\n        data VARCHAR2(100)\n    ) ON COMMIT DELETE ROWS"; // Use ON COMMIT PRESERVE ROWS if you want to keep data until session ends\n\n$stid_create = oci_parse($conn, $query_create);\noci_execute($stid_create);\n\n// Insert data into the GTT\n$query_insert = "INSERT INTO my_temp_table (id, data) VALUES (:id, :data)";\n$stid_insert = oci_parse($conn, $query_insert);\noci_bind_by_name($stid_insert, ':id', $id);\noci_bind_by_name($stid_insert, ':data', $data);\n\n$id = 1;\n$data = 'Sample Data';\noci_execute($stid_insert);\n\n// Retrieve data from the GTT\n$query_select = "SELECT * FROM my_temp_table";\n$stid_select = oci_parse($conn, $query_select);\noci_execute($stid_select);\n\nwhile (($row = oci_fetch_array($stid_select, OCI_ASSOC)) != false) {\n    print_r($row);\n}\n\n// Clean up\noci_free_statement($stid_insert);\noci_free_statement($stid_select);\noci_close($conn);\n?>