public function findAll($constraints = array()) {\n    // Select all records\n    $SQL = 'SELECT * FROM ' . $this->tableName;\n\n    // See if there's any constraints\n    if (count($constraints) > 0) {\n        $SQL .= ' WHERE ';\n\n        foreach ($constraints as $field => $value) {\n            $SQL .= $field . ' = :' . $field . ' AND ';\n        }\n\n        // Remove the final AND\n        $SQL = substr($SQL, 0, -5);\n    }\n\n    // Prepare the statement\n    $PDOStatement = $this->PDO->prepare($SQL);\n\n    // Loop through constraints and bind values\n    foreach ($constraints as $field => $value) {\n        print 'Binding ' . $field . ' to ' . $value . ' <br />';\n        $PDOStatement->bindValue(':' . $field, $value);\n    }\n\n    $PDOStatement->execute();\n\n    while ($results = $PDOStatement->fetch(PDO::FETCH_ASSOC)) {\n        var_dump($results);\n    }\n}