<?php\n// URL of the remote file\n$url = 'http://example.com/path/to/file';\n\n// Get headers\n$headers = get_headers($url, 1);\n\n// Check if Last-Modified is present\nif (isset($headers['Last-Modified'])) {\n    // Get the Last-Modified header value\n    $lastModifiedGmt = $headers['Last-Modified'];\n\n    // Create a DateTime object from the GMT time\n    $dateTimeGmt = new DateTime($lastModifiedGmt, new DateTimeZone('GMT'));\n\n    // Define the timezone for Australia/Adelaide\n    $adelaideTimeZone = new DateTimeZone('Australia/Adelaide');\n\n    // Convert the DateTime object to Adelaide timezone\n    $dateTimeGmt->setTimezone($adelaideTimeZone);\n\n    // Format the date to match Apache's directory listing format\n    echo $dateTimeGmt->format('d-M-Y H:i');\n} else {\n    echo 'Last-Modified header not found.';\n}\n?>