$sql_photo = "SELECT * FROM friend_user_photo WHERE userid = :user_id AND defaultphoto = 'yes' ORDER BY auto_id DESC";\n$result_photo = executeQuery($sql_photo, array(':user_id' => $user_id));\n\nfunction executeQuery($sql, $params = array()) {\n    $conn = oci_connect('username', 'password', 'hostname/dbname'); // Update with your Oracle DB credentials\n\n    if (!$conn) {\n        $e = oci_error();\n        die('<BR><center><font size="+1" face="arial" color="red">An Internal Error has Occured.<BR> The error has been recorded for review</font></center><br>');\n    }\n\n    $stmt = oci_parse($conn, $sql);\n\n    // Bind parameters\n    foreach ($params as $key => $val) {\n        oci_bind_by_name($stmt, $key, $params[$key]);\n    }\n\n    oci_execute($stmt);\n    \n    if ($e = oci_error($stmt)) {\n        $error = '<BR><center><font size="+1" face="arial" color="red">An Internal Error has Occured.<BR> The error has been recorded for review</font></center><br>';\n        if($_SESSION['auto_id'] == 1){\n            $sql_formatted = highlight_string($sql, true);\n            $error .= '<b>The Oracle SQL Syntax Used</b><br>' . $sql_formatted . '<br><br><b>The Oracle Error Returned</b><br>' . $e['message'];\n        }\n        die($error);\n    }\n\n    $result = array();\n    while ($row = oci_fetch_assoc($stmt)) {\n        $result[] = $row;\n    }\n\n    oci_free_statement($stmt);\n    oci_close($conn);\n\n    return $result;\n}