<?php\n   $secret_key = 'your_secret_key';\n\n   function generate_hmac($data, $key) {\n       return hash_hmac('sha256', $data, $key);\n   }\n\n   // When receiving a score submission\n   $data = $_POST['data'];  // Example: score=12345&user_id=67890\n   $received_hmac = $_POST['hmac'];\n\n   $calculated_hmac = generate_hmac($data, $secret_key);\n\n   if (hash_equals($calculated_hmac, $received_hmac)) {\n       // Process the score\n   } else {\n       // Invalid request\n       die('Invalid request');\n   }\n   ?>