<?php\nrequire_once 'VersionControl/SVN.php';\n\n$options = array(\n    'fetchmode' => VERSIONCONTROL_SVN_FETCHMODE_ASSOC,\n    'svn_path' => '/usr/bin/svn', // Adjust the path to your SVN binary\n);\n\n$svn = VersionControl_SVN::factory('export', $options);\n\n$svnUrl = 'http://your-svn-repository-url/trunk/';\n$localPath = '/path/to/export/directory';\n\ntry {\n    $svn->run(array($svnUrl, $localPath));\n    echo "Export successful!";\n} catch (VersionControl_SVN_Exception $e) {\n    echo "Export failed: ", $e->getMessage();\n}\n?>