<?php\n    // Set the locale based on user preference or default\n    $lang = 'fr_FR'; // Example: dynamically set based on user's choice\n    putenv("LC_ALL=$lang");\n    setlocale(LC_ALL, $lang);\n\n    // Specify the location of translation files\n    bindtextdomain("messages", "./locale");\n    textdomain("messages");\n\n    // Example usage\n    echo _("Hello, world!");\n    ?>