<?php\n$price = 999.88;\n$str = "string";\n$string = "this is a %f sample %'-20s,<br> this string is mixed with type specifier like (number:%d's)";        \n\n// Regular expression to match type specifiers\n$pattern = '/%[-0-9\'.]*[bcdeEfFgGosuxX]/';\n\n// Replacing type specifiers with [#]\n$result = preg_replace($pattern, '[#]', $string);\n\n// Output the result\necho $result;\n?>