function generateAccNo() {\n    do {\n        // Generate a 9-digit account number\n        $accountNumber = mt_rand(100000000, 999999999);\n\n        // Check if the account number already exists in the database\n        $exists = checkIfAccountExistsInDB($accountNumber);\n\n    } while ($exists);\n\n    return $accountNumber;\n}\n\nfunction checkIfAccountExistsInDB($accountNumber) {\n    // This function should query the database to check if the account number exists\n    // Example using PDO (assuming $pdo is your PDO instance):\n    $stmt = $pdo->prepare("SELECT COUNT(*) FROM accounts WHERE account_number = :account_number");\n    $stmt->execute(['account_number' => $accountNumber]);\n    return $stmt->fetchColumn() > 0;\n}