<?php\nfunction getWeekdaysCount($startDate, $endDate) {\n    $start = new DateTime($startDate);\n    $end = new DateTime($endDate);\n    $end->modify('+1 day'); // to include the end date in the count\n\n    $interval = new DateInterval('P1D');\n    $dateRange = new DatePeriod($start, $interval, $end);\n\n    $weekdays = 0;\n\n    foreach ($dateRange as $date) {\n        // Check if the day is not a Saturday (6) or Sunday (0)\n        if ($date->format('N') < 6) {\n            $weekdays++;\n        }\n    }\n\n    return $weekdays;\n}\n\n// Example usage\n$startDate = '2008-08-31';\n$endDate = '2008-09-13';\necho getWeekdaysCount($startDate, $endDate); // Output: 10\n?>