class PageAttributes \n{\n    private $db_connection;\n    private $page_title;\n\n    public function __construct($db_connection) \n    {\n        $this->db_connection = $db_connection;\n        $this->page_title = '';\n    }\n\n    public function get_page_title()\n    {\n        return $this->page_title;\n    }\n\n    public function set_page_title($page_title)\n    {\n        $this->page_title = $page_title;\n    }\n}\n\n// Assumed $db_connection is already defined\n$db_connection = new mysqli('your_host', 'your_user', 'your_password', 'your_database');\n\n$objPage = new PageAttributes($db_connection);\n\nfunction page_properties($objPage, $title) \n{    \n    $objPage->set_page_title($title);\n}\n\n// Assuming $myrow['title'] is already defined\n$myrow = ['title' => 'Home Page'];\n\npage_properties($objPage, $myrow['title']);