<?php\nfunction getSvnRevision() {\n    // Execute the SVN info command and capture the output\n    $output = [];\n    exec('svn info', $output);\n\n    // Find the line that contains the revision number\n    foreach ($output as $line) {\n        if (strpos($line, 'Revision:') !== false) {\n            // Split the line by spaces and get the revision number\n            $parts = explode(':', $line);\n            if (isset($parts[1])) {\n                return trim($parts[1]);\n            }\n        }\n    }\n    return null; // Return null if the revision number is not found\n}\n\n$revision = getSvnRevision();\necho "Current SVN Revision: " . $revision;\n?>