function isTextile($input) {\n    // Simple heuristic for Textile detection\n    $textilePatterns = [\n        '/\b_\b/', // Emphasis\n        '/\b\*\b/', // Strong emphasis\n        '/\b#\b/', // Ordered list\n        '/\b\b/', // Unordered list\n        '/"(.*?)":(http|https|ftp|mailto):\/\/[^\s]+/', // Hyperlink\n    ];\n    foreach ($textilePatterns as $pattern) {\n        if (preg_match($pattern, $input)) {\n            return true;\n        }\n    }\n    return false;\n}\n\nfunction isMarkdown($input) {\n    // Simple heuristic for Markdown detection\n    $markdownPatterns = [\n        '/_([^_]+)_/', // Emphasis\n        '/\*([^\*]+)\*/', // Strong emphasis\n        '/\d+\.\s/', // Ordered list\n        '/-\s/', // Unordered list\n        '/\[(.*?)\]\((http|https|ftp|mailto):\/\/[^\s]+\)/', // Hyperlink\n    ];\n    foreach ($markdownPatterns as $pattern) {\n        if (preg_match($pattern, $input)) {\n            return true;\n        }\n    }\n    return false;\n}\n\nfunction detectFormat($input) {\n    $isTextileFormat = isTextile($input);\n    $isMarkdownFormat = isMarkdown($input);\n\n    if ($isTextileFormat && !$isMarkdownFormat) {\n        return 'textile';\n    } elseif (!$isTextileFormat && $isMarkdownFormat) {\n        return 'markdown';\n    } else {\n        // Handle ambiguous cases or fallback\n        return 'unknown';\n    }\n}\n\n// Example usage\n$userInput = "Your input text here";\n$format = detectFormat($userInput);\necho "Detected format: $format";