function formatDuration($time) {\n    list($hours, $minutes, $seconds) = explode(':', $time);\n\n    $formatted = '';\n\n    if ($hours > 0) {\n        $formatted .= $hours . ' hours ';\n    }\n\n    $formatted .= $minutes . ' minutes ' . $seconds . ' seconds';\n\n    return $formatted;\n}\n\n// Example usage\n$time = "00:45:30";\necho formatDuration($time); // Output: 45 minutes 30 seconds