function truncateString($string, $limit = 25) {\n    if (strlen($string) > $limit) {\n        return substr($string, 0, $limit) . '...';\n    } else {\n        return $string;\n    }\n}\n\n$text = '12345678901234567890abcdefg';\n$truncatedText = truncateString($text);\n\necho $truncatedText; // Output: 12345678901234567890abcde...