class XMLReaderWithExceptions extends XMLReader {\n    public function read() {\n        if (!parent::read()) {\n            $error = libxml_get_last_error();\n            if ($error) {\n                throw new Exception($error->message, $error->code);\n            }\n        }\n        return true;\n    }\n}\n\n$reader = new XMLReaderWithExceptions();\n$reader->open('file.xml');\n\ntry {\n    while ($reader->read()) {\n        if ($reader->nodeType == XMLReader::ELEMENT && $reader->name == 'elementName') {\n            echo $reader->readInnerXml();\n        }\n    }\n} catch (Exception $e) {\n    echo 'XML Error: ', $e->getMessage();\n}\n\n$reader->close();