<?php\n// Directory where the temporary files are stored\n$temp_dir = __DIR__; // Change this if your files are stored in a different directory\n\n// Get all files in the directory\n$files = scandir($temp_dir);\n\nforeach ($files as $file) {\n    // Check if the file is a temporary file and has a corresponding '.time' file\n    if (strpos($file, 'temp_') === 0 && file_exists("$file.time")) {\n        $creation_time = file_get_contents("$file.time");\n        $current_time = time();\n        \n        // Check if the file is older than 2 minutes (120 seconds)\n        if (($current_time - $creation_time) > 120) {\n            // Delete the file and its corresponding '.time' file\n            unlink($file);\n            unlink("$file.time");\n        }\n    }\n}\n?>