function checkdnsrr_custom($host, $type = 'MX') {\n    $dns_records = dns_get_record($host, constant('DNS_' . strtoupper($type)));\n    return !empty($dns_records);\n}\n\n// Example usage\necho ("test.com dns check: " . (checkdnsrr_custom("test.com", "NS") ? 'true' : 'false')); // should return true if records exist\nprint_r(dns_get_record("test.com", DNS_NS)); // returns the right data