// In your Person model (or Table class)\npublic function afterSave($event, $entity, $options)\n{\n    if ($entity->isDirty('source_id')) {\n        // Fetch the original value of the source_id before the save\n        $originalSourceId = $entity->getOriginal('source_id');\n\n        // Decrement the count for the old source_id\n        if (!empty($originalSourceId)) {\n            $this->Sources->updateAll(\n                ['Person_Count = Person_Count - 1'],\n                ['id' => $originalSourceId]\n            );\n        }\n\n        // Increment the count for the new source_id\n        $newSourceId = $entity->source_id;\n        if (!empty($newSourceId)) {\n            $this->Sources->updateAll(\n                ['Person_Count = Person_Count + 1'],\n                ['id' => $newSourceId]\n            );\n        }\n    }\n}