<?php\n// Establish a connection to the database using PDO\n$dsn = 'mysql:host=your_host;dbname=your_dbname;charset=utf8';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    // Set the PDO error mode to exception\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare an SQL statement with a parameter placeholder\n    $stmt = $pdo->prepare("INSERT INTO `table` (`column`) VALUES (:user_input)");\n\n    // Bind the user input to the parameter placeholder safely\n    $stmt->bindParam(':user_input', $_POST['user_input'], PDO::PARAM_STR);\n\n    // Execute the prepared statement\n    $stmt->execute();\n\n    echo "Record inserted successfully";\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>