// Include HTMLPurifier library\n    require_once '/path/to/htmlpurifier/library/HTMLPurifier.auto.php';\n\n    // Getting the input and sanitizing with HTMLPurifier\n    $config = HTMLPurifier_Config::createDefault();\n    $purifier = new HTMLPurifier($config);\n    $htmlContent = filter_input(INPUT_POST, 'htmlContent', FILTER_UNSAFE_RAW);\n    $cleanHtmlContent = $purifier->purify($htmlContent);\n\n    // Saving in the database\n    $stmt = $pdo->prepare("INSERT INTO posts (html_content) VALUES (:html_content)");\n    $stmt->execute(['html_content' => $cleanHtmlContent]);\n\n    // Displaying\n    echo $cleanHtmlContent;