function mapObject($object, callable $callback) {\n    foreach (get_object_vars($object) as $property => $value) {\n        if (is_string($value)) {\n            $object->$property = $callback($value);\n        }\n    }\n    return $object;\n}\n\n// Example usage:\nclass Example {\n    public $name = "John Doe";\n    public $age = 30;\n    public $email = "john@example.com";\n}\n\n$obj = new Example();\n\n$modifiedObj = mapObject($obj, function($value) {\n    return strtoupper($value); // Example callback function to convert strings to uppercase\n});\n\nprint_r($modifiedObj);