function sort_by_column(&$array, $column_index)\n{\n  usort($array, function($a, $b) use ($column_index) {\n    $dateA = strtotime($a[$column_index]);\n    $dateB = strtotime($b[$column_index]);\n    return $dateB - $dateA; // Sort in descending order\n  });\n}\n\n// Example usage:\n$data = f_parse_csv('data.csv', 1000, ',');\nsort_by_column($data, 1); // Assuming date is in the second column (index 1)\n\n// Print sorted data\nprint_r($data);