<?php\n// Start the session\nsession_start();\n\n// Regenerate session ID to prevent session fixation attacks\nif (!isset($_SESSION['initiated'])) {\n    session_regenerate_id(true);\n    $_SESSION['initiated'] = true;\n}\n\n// Set session variables\n$_SESSION['username'] = 'exampleUser';\n\n// Secure session settings (can also be set in php.ini)\nini_set('session.cookie_httponly', 1); // Prevent JavaScript access to session cookie\nini_set('session.cookie_secure', 1);   // Ensure the session cookie is only sent over HTTPS\nini_set('session.use_strict_mode', 1); // Use strict mode to prevent session fixation\nini_set('session.use_only_cookies', 1); // Prevent session ID from being passed in URLs\n\n// Example usage of session variables\necho "Hello, " . $_SESSION['username'];\n?>